import React, { useState } from "react";
import { Card, CardContent, TextField, Button, Typography } from "@mui/material";

const Chatbot = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");

    const sendMessage = async () => {
        if (!input.trim()) return;

        setMessages([...messages, { text: input, sender: "user" }]);

        try {
            const response = await fetch("http://127.0.0.1:5000/chat", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ session_id: "user1", query: input })
            });

            const data = await response.json();
            setMessages([...messages, { text: input, sender: "user" }, { text: data.response, sender: "bot" }]);
        } catch (error) {
            console.error("Error:", error);
            setMessages([...messages, { text: "Error occurred", sender: "bot" }]);
        }

        setInput("");
    };


    return (
        <Card sx={{ maxWidth: 400, margin: "auto", mt: 5, boxShadow: 3 }}>
            <CardContent>
                <Typography variant="h6" align="center">Chatbot</Typography>
                <div style={{ height: 300, overflowY: "auto", marginBottom: 10 }}>
                    {messages.map((msg, index) => (
                        <Typography key={index} sx={{
                            textAlign: msg.sender === "user" ? "right" : "left",
                            backgroundColor: msg.sender === "user" ? "#007bff" : "#f1f1f1",
                            color: msg.sender === "user" ? "white" : "black",
                            padding: "8px",
                            borderRadius: "10px",
                            margin: "5px 0",
                            display: "inline-block",
                            maxWidth: "80%"
                        }}>
                            {msg.text}
                        </Typography>
                    ))}
                </div>
                <TextField
                    fullWidth
                    variant="outlined"
                    placeholder="Type a message..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                />
                <Button fullWidth variant="contained" color="primary" onClick={sendMessage} sx={{ mt: 2 }}>
                    Send
                </Button>
            </CardContent>
        </Card>
    );
};

export default Chatbot;
