import React, { useState, useEffect, useRef } from "react";
import { Card, CardContent, TextField, Button, Typography, IconButton } from "@mui/material";
import ChatIcon from "@mui/icons-material/Chat";
import CloseIcon from "@mui/icons-material/Close";

const Chatbot = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [isOpen, setIsOpen] = useState(false);
    const chatBoxRef = useRef(null);

    useEffect(() => {
        if (chatBoxRef.current) {
            chatBoxRef.current.scrollTop = chatBoxRef.current.scrollHeight;
        }
    }, [messages]);

    const toggleChat = () => {
        setIsOpen(!isOpen);
    };

    const sendMessage = async () => {
        if (!input.trim()) return;

        const userMessage = { text: input, sender: "user" };
        setMessages((prevMessages) => [...prevMessages, userMessage]);
        setInput(""); // Clears input immediately after sending

        try {
            const response = await fetch("http://127.0.0.1:5000/chat", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ session_id: "user1", query: input })
            });

            const data = await response.json();
            const botMessage = { text: data.response, sender: "bot" };
            setMessages((prevMessages) => [...prevMessages, botMessage]);
        } catch (error) {
            console.error("Error:", error);
            setMessages((prevMessages) => [...prevMessages, { text: "Error occurred", sender: "bot" }]);

        }

    };


    return (
        <>
            {/* Floating Button to Toggle Chat */}
            <IconButton 
                onClick={toggleChat} 
                sx={{
                    position: "fixed",
                    bottom: 20,
                    right: 20,
                    backgroundColor: "#007bff",
                    color: "white",
                    "&:hover": { backgroundColor: "#0056b3" }
                }}
            >
                {isOpen ? <CloseIcon /> : <ChatIcon />}
            </IconButton>

            {/* Chatbot Popup */}
            {isOpen && (
                <Card sx={{
                    position: "fixed",
                    bottom: 80,
                    right: 20,
                    width: 350,
                    height: 450,
                    boxShadow: 5,
                    display: "flex",
                    flexDirection: "column",
                    transition: "transform 0.3s ease-in-out"
                }}>
                    <CardContent sx={{ flex: 1, display: "flex", flexDirection: "column" }}>
                        <Typography variant="h6" align="center">Chatbot</Typography>
                        <div ref={chatBoxRef} style={{ flex: 1, overflowY: "auto", padding: "10px" }}>
                            {messages.map((msg, index) => (
                                <div key={index} style={{
                                    display: "flex",
                                    justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
                                    marginBottom: "10px"
                                }}>
                                    <Typography sx={{
                                        backgroundColor: msg.sender === "user" ? "#007bff" : "#f1f1f1",
                                        color: msg.sender === "user" ? "white" : "black",
                                        padding: "10px",
                                        borderRadius: "15px",
                                        maxWidth: "75%",
                                        wordWrap: "break-word",
                                        textAlign: "left",
                                        lineHeight: "1.4"
                                    }}>
                                        {msg.text}
                                    </Typography>
                                </div>
                            ))}
                        </div>
                        <TextField
                            fullWidth
                            variant="outlined"
                            placeholder="Type a message..."
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                        />
                        <Button fullWidth variant="contained" color="primary" onClick={sendMessage} sx={{ mt: 1 }}>
                            Send
                        </Button>
                    </CardContent>
                </Card>
            )}
        </>
    );
};


export default Chatbot;
